import React, { Component } from 'react';
import GoogleLogin from 'react-google-login'
import './login-form.css';

export default class LoginForm extends Component {

    responseGoogle = (resp) => {
        console.log(resp);
        console.log(resp.profileObj);
        // if (resp.profileObj.email)
        this.setState({
            email: resp.profileObj.email,
            name: resp.profileObj.givenName,
            surname: resp.profileObj.familyName,
            profilePic: resp.profileObj.imageUrl
        });
        alert(`email: ${this.state.email}\nname: ${this.state.name} ${this.state.surname}`);
    }

    return() {
        <div>
            <form action="/" class="d-flex flex-column">
                <div class="form-group p-0 mb-3">
                    <label for="inputEmail" className='label-sm'>Email</label>
                    <input type="email" class="form-control fw-lighter" id="inputEmail" aria-describedby="emailHelp" placeholder="Введите свой email"/>
                    <small id="emailHelp" class="form-text text-muted">Мы ни с кем не делимся вашей почтой</small>
                </div>
                <div class="form-group p-0 mb-2">
                    <label for="inputPassword">Пароль</label>
                    <input type="password" class="form-control" id="inputPassword" placeholder="&bull;&bull;&bull;&bull;&bull;&bull;&bull;&bull;&bull;&bull;&bull;&bull;"/>
                </div>
                <div class="d-flex justify-content-center flex-wrap align-content-center p-0 mb-2">
                    <div class="form-check p-0 me-auto order-1">
                        <input type="checkbox" class="form-check-input ms-0" id="check"/>
                        <label class="form-check-label ms-2 fw-bolder text-sm" for="check">Запомнить меня</label>
                    </div>
                    <div class="form-group p-0 order-2">
                        <a href="/" class="link text-left fw-bolder text-sm">Забыл пароль</a>
                    </div>
                </div>
                <div class="d-flex flex-column">
                    <button type="button" class="btn btn-primary position-relative text-capitalize p-2 m-0 mb-2 btn-sm container-fluid waves-effect">Войти</button>
                    <GoogleLogin
                        clientId='744713136880-p4eq0bblccrntia52ge51kescquir0li.apps.googleusercontent.com'
                        buttonText='Войти через Google'
                        onSuccess={this.responseGoogle}
                        onFailure={this.responseGoogle}
                        cookiePolicy={'single_host_origin'}
                        isSignedIn={true}
                        render={renderProps => (
                            <button onClick={renderProps.onClick} className={'btn btn-dark position-relative text-capitalize p-2 m-0 mt-1 btn-sm container-fluid'}>Войти через Google</button>
                        )}
                    />
                </div>
            </form>
            <div class="mt-3 container-fluid">
                <p class="text-center text-sm">Нет учетной записи? <a href="http://" class="form">Зарегистрируйтесь!</a></p>
            </div>
        </div>
    };
};
