import React, { Component } from 'react'
import GoogleLogin from 'react-google-login'
import axios from 'axios'
import LoginHeader from '../login-header/login-header'
import LoginForm from '../login-form/login-form'

const customStyle = {
    position: 'absolute',
    textAlign: 'center',
    width: '100%'
}

const getData = async () => {
    const res = await axios.get('https://geolocation-db.com/json/')
    // console.log(res.data);
    return res.data;
}

export default class App extends Component {
  state = {
    email: 'none',
    name: 'none',
    surname: 'none',
    profilePic: ''
  }

  returnData = () => {
    getData().then((data) => {
        alert(`IP: ${data.IPv4}\nCity: ${data.city}\nCountry: ${data.country_name}\nLatitude: ${data.latitude}\nLongtitude: ${data.longitude}`)
    })
  }

  responseGoogle = (resp) => {
    console.log(resp);
    console.log(resp.profileObj);
    // if (resp.profileObj.email)
    this.setState({
        email: resp.profileObj.email,
        name: resp.profileObj.givenName,
        surname: resp.profileObj.familyName,
        profilePic: resp.profileObj.imageUrl
    });
    alert(`email: ${this.state.email}\nname: ${this.state.name} ${this.state.surname}`);
    getData()
}

  render() {
    return (
        <div className="container">
        <div className="position-absolute top-50 start-50 translate-middle">
            <LoginHeader />
            <LoginForm onSubmit={this.onSignedIn}/>
        </div>
    </div>
    )
  }
}
