import React, { Component } from 'react'
import axios from 'axios'
import LoginHeader from '../login-header/login-header'
import LoginForm from '../login-form/login-form'
import Sidebar from '../sidebar/sidebar'

const customStyle = {
    position: 'absolute',
    textAlign: 'center',
    width: '100%'
}

const getData = async () => {
    const res = await axios.get('https://geolocation-db.com/json/')
    // console.log(res.data);
    return res.data;
}

export default class App extends Component {
  state = {
    email: null,
    name: null,
    profilePic: null
  }

  onSubmitted = (resp) => {
    // console.log(resp);
    // console.log(resp.profileObj);
    if(resp.profileObj.email.substr(-13) == '@phystech.edu') {
      this.setState({
        email: resp.profileObj.email,
        username: resp.profileObj.name,
        profilePic: resp.profileObj.imageUrl,
      });
    } else {
      alert('Зарегистрируйтесь через почту @phystech.edu')
    }

  }

  render() {
    return (
      <React.Fragment>
        <Sidebar data={this.state}/>
      <div className="container">
        <div className="position-absolute top-50 start-50 translate-middle">
            <LoginHeader />
            <LoginForm onSubmitted={this.onSubmitted}/>
        </div>
      </div>
      </React.Fragment>
    )
  }
}
