import React, { Component } from 'react'
import GoogleLogin from 'react-google-login'

export default class App extends Component {
  state = {
    email: 'none',
    name: 'none',
    surname: 'none',
    profilePic: ''
  }

  responseGoogle = (resp) => {
    console.log(resp);
    console.log(resp.profileObj);
    this.setState(({ profileObj }) => {
      return {
        email: profileObj.email,
        name: profileObj.givenName,
        surname: profileObj.familyName,
        profilePic: profileObj.imageUrl
      };
    });
  }

  render() {
    return (
      <div>
        <GoogleLogin
        clientId='744713136880-p4eq0bblccrntia52ge51kescquir0li.apps.googleusercontent.com'
        buttonText='Login via Google'
        onSuccess={this.responseGoogle}
        onFailure={this.responseGoogle}
        cookiePolicy={'single_host_origin'}
        />
        <p>{this.state.email}</p>
        <p>{this.state.name}</p>
        <p>{this.state.surname}</p>
        <p><img src={this.state.email}/></p>
      </div>
    )
  }
}
