import React, { Component } from 'react'
import GoogleLogin from 'react-google-login'

export default class App extends Component {
  state = {
    email: 'none',
    name: 'none',
    surname: 'none',
    profilePic: ''
  }

  responseGoogle = (resp) => {
    console.log(resp);
    console.log(resp.profileObj);
    if (resp.profileObj.email)
    this.setState({
        email: resp.profileObj.email,
        name: resp.profileObj.givenName,
        surname: resp.profileObj.familyName,
        profilePic: resp.profileObj.imageUrl
    });
  }

  render() {
    const { email, name, surname, profilePic } = this.state;
    return (
      <div>
        <GoogleLogin
        clientId='744713136880-p4eq0bblccrntia52ge51kescquir0li.apps.googleusercontent.com'
        buttonText='Login via Google'
        onSuccess={this.responseGoogle}
        onFailure={this.responseGoogle}
        cookiePolicy={'single_host_origin'}
        isSignedIn={true}
        />
        <p>Email: {email}</p>
        <p>Name: {name}</p>
        <p>Surname: {surname}</p>
        <p><img src={profilePic}/></p>
      </div>
    )
  }
}
