import React, { Component } from 'react';

export default class Popup extends Component {
    state = {  } 
    render() { 
        return (
            <React.Fragment>
            <div className="darker-background" id="popup-alert" style="display: none;">
                <div className="popup-body position-absolute top-50 start-50 translate-middle text-center pt-3">
                    <div className="header">
                        <h5 className="h5 fw-bold">Ошибка</h5>
                    </div>
                    <div className="container ps-3 pe-3">
                        <p>При попытке войти произошла ошибка. Пожалуйста, попробуйте позднее!</p>
                    </div>
                    <div className="footer d-flex justify-content-around row flex-wrap">
                        {/* <div className="col-6 fw-bolder text-danger footer-button pt-2 pb-2" id="popup-delete">Удалить</div> */}
                        <div className="col-12 fw-bolder text-faki footer-button pt-2 pb-2" id="popup-close">Закрыть</div>
                    </div>
                </div>
            </div>

            <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
            <script>
                document.querySelector(document).ready(function(){
                    document.querySelector('#popup-alert').fadeIn(100)
                });
                document.querySelector('#popup-close').click(function(){
                    document.querySelector('#popup-alert').fadeOut(100)
                });
            </script>
            </React.Fragment>
        );
    }
}
